/**
 * Clase adaptadora para subir imgenes desde CKEditor al servidor.
 * Maneja la subida de archivos de imagen al endpoint /ckeditor-upload-image.
 *
 * @class AdaptadorSubida
 * @author Jos Manuel Jimnez
 */
class AdaptadorSubida {
    constructor(cargador) {
        this.cargador = cargador;
    }

    upload() {
        return this.cargador.file
            .then(archivo => new Promise((resolver, rechazar) => {
                this._iniciarPeticion();
                this._iniciarEscuchadores(resolver, rechazar, archivo);
                this._enviarPeticion(archivo);
            }));
    }

    abort() {
        if (this.xhr) {
            this.xhr.abort();
        }
    }

    _iniciarPeticion() {
        const xhr = this.xhr = new XMLHttpRequest();
        xhr.open('POST', '/ckeditor-upload-image', true);
        xhr.setRequestHeader('X-CSRF-TOKEN', document.querySelector('meta[name="csrf-token"]').getAttribute('content'));
        xhr.responseType = 'json';
    }

    _iniciarEscuchadores(resolver, rechazar, archivo) {
        const xhr = this.xhr;
        const cargador = this.cargador;
        const texto_error_generico = 'No se pudo subir el archivo: ' + archivo.name + '.';

        xhr.addEventListener('error', () => rechazar(texto_error_generico));
        xhr.addEventListener('abort', () => rechazar());
        xhr.addEventListener('load', () => {
            const respuesta = xhr.response;

            if (!respuesta || respuesta.error) {
                return rechazar(respuesta && respuesta.error ? respuesta.error.message : texto_error_generico);
            }

            resolver({
                default: respuesta.url
            });
        });

        if (xhr.upload) {
            xhr.upload.addEventListener('progress', evento => {
                if (evento.lengthComputable) {
                    cargador.uploadTotal = evento.total;
                    cargador.uploaded = evento.loaded;
                }
            });
        }
    }

    _enviarPeticion(archivo) {
        const datos = new FormData();
        datos.append('upload', archivo);
        this.xhr.send(datos);
    }
}

/**
 * Plugin para CKEditor que configura el adaptador de subida de imgenes.
 *
 * @param {object} editor Instancia del editor CKEditor.
 * @returns {void}
 * @author Jos Manuel Jimnez
 */
function pluginAdaptadorSubida(editor) {
    editor.plugins.get('FileRepository').createUploadAdapter = (cargador) => {
        return new AdaptadorSubida(cargador);
    };
}

/**
 * Diccionario para almacenar instancias de editores CKEditor.
 * La clave es el ID del elemento textarea y el valor es la instancia del editor.
 *
 * @type {object}
 */
let editores_classic = {};

/**
 * Inicializar CKEditor Classic en todos los elementos con la clase 'classic-editor-opciones'.
 * Configura el plugin de subida de imgenes para evitar que se guarden como base64.
 *
 * @returns {void}
 * @author Jos Manuel Jimnez
 * @author Andres Gonzalez
 */
function inicializarClassicEditor() {
    document.querySelectorAll('.classic-editor-opciones').forEach(elemento => {
        if (!editores_classic[elemento.id]) {
            ClassicEditor.create(elemento, {
                extraPlugins: [pluginAdaptadorSubida],
                toolbar: {
                    items: [
