/**
 * Funcin generica para inicializar el validate() y el submit() de un formulario
 *
 * @param {string} formulario id del form HTML que se desea validar.
 * @returns {void}
 * @author Jos Manuel Jimnez
 * @author Andres Morales
 * @author Andres Gonzalez
 */
function configurarFormulario(formulario) {
    let $formulario = $('#' + formulario);
    if (!$formulario.length) {
        return;
    }
    let redirige_form = $formulario.attr('data-url');
    let evitar_id_url = $formulario.data('sinIdUrl');
    if (!$formulario.data('validator')) {
        $formulario.validate();
    }
    $formulario.off("submit.formularios");
    $formulario.on("submit.formularios", function (event) {
        if ($('#' + formulario).attr('action') !== undefined) {
            resource_url = $('#' + formulario).attr('action');
        }
        $('#contenedor-alert-toast').empty();
        event.preventDefault();
        let camposHidden = $("#" + formulario + " input[type='hidden']");

        let camposHiddenVacios = camposHidden.filter(function () {
            return ($(this).val() === "" || $(this).val() === "[]") && $(this).prop('required');
        });
        let validoAdicional = true;
        if (window.validacionAdicional) { validoAdicional = validacionAdicional(formulario); }
        if ($(this).valid() && camposHiddenVacios.length == 0 && validoAdicional) {
            let id_form = $("#" + formulario + " #id").val();
            let metodo = (id_form === undefined || id_form.length == 0) ? 'POST' : 'PUT';
            if (evitar_id_url && id_form !== undefined && id_form.length > 0) {
                metodo = 'POST';
            }
            let url_form = `${resource_url}`;
            if (!evitar_id_url) {
                url_form = (id_form === undefined || id_form.length == 0) ? `${resource_url}` : `${resource_url}/${id_form}`;
            }
            let accion_realizada = (id_form === undefined || id_form.length == 0) ? 'cre' : 'actualiz';
            let datos_form = new FormData();
            if ($('#' + formulario).attr('method') !== undefined) {
                metodo = $('#' + formulario).attr('method');
            }
            datos_form.append('_method', metodo);
            $('#' + formulario).find(':input:not(.input-dinamico):not(button), select:not(.input-dinamico), textarea:not(.input-dinamico)').each(function () {
                if ($(this).attr('type') === 'file' && $(this)[0].files.length > 0) {
                    //Recorre los archivos adjuntos para enviarlos en los datos del form.
                    if ($(this).hasClass('input-form')) {
                        if($(this).attr('multiple')){
                            let archivos = $(this)[0].files;
                            for (let i = 0; i < archivos.length; i++) {
                                datos_form.append($(this).attr('name'), archivos[i]);
                            }
                        }else{
                            datos_form.append($(this).attr('name'), $(this)[0].files[0]);
                        }
                    } else {
                        let archivos = $(this)[0].files;
                        for (let i = 0; i < archivos.length; i++) {
                            datos_form.append('archivos[]', archivos[i]);
                        }
                    }
                } else if ($(this).is('select[multiple]')) {
                    $(this).find('option:selected').each(function () {
                        datos_form.append($(this).parent().attr('name'), $(this).val());
                    });
                } else if ($(this).attr('type') === 'checkbox') {
                    if ($(this).is(':checked')) {
                        datos_form.append($(this).attr('name'), $(this).val());
                    }
                } else if ($(this).attr('type') === 'radio') {
                    if ($(this).is(':checked')) {
                        datos_form.append($(this).attr('name'), $(this).val());
                    }
                } else if ($(this).hasClass('cleave')) {
                    datos_form.append($(this).attr('name'), limpiarCleave($(this).val()));
                } else if ($(this).val()!=''){
                    datos_form.append($(this).attr('name'), $(this).val());
                } else if ($("#" + formulario + " #inputs_vacios").val() === "true") {
                    datos_form.append($(this).attr('name'), $(this).val());
                } 
            });

            datos_form.delete('inputs_vacios');
            datos_form.append('formulario_id_procesar', formulario);
            if (window.agregarDataForm) { datos_form = agregarDataForm(datos_form); }
            datos_form.delete('formulario_id_procesar');

            //Si la vista es externa se actualiza la variable url_form
            if (redirige_form !== undefined) {
                url_form = (id_form === undefined || id_form.length == 0) ? `${redirige_form}` : `${redirige_form}/${id_form}`;
            }

            $.ajax({
                type: 'POST',
                url: url_form,
                data: datos_form,
                contentType: false,
                processData: false,
                success: function (respuesta) {
                    respuesta.formulario = formulario;
                    respuesta.accion_realizada = accion_realizada;

                    if (formulario === 'formulario-soportes' && window.postSuccessSoportes) {
                        // Caso especial para el formulario de soportes
                        postSuccessSoportes(respuesta);
                    } else if (window.postSuccess) {
                        postSuccess(respuesta);
                        limpiarFormulario('formulario')
                        mostrarToast('success', `El registro se ${accion_realizada} con xito`);
                    } else {
                        limpiarFormulario(formulario);
                        mostrarToast('success', `El registro se ${accion_realizada} con xito`);
                    }

                    if (redirige_form !== undefined) {
                        window.location.href = redirige_form;
                    } else {
                        actualizarDatatables();
                    }
                },
                error: function (error) {
                    if (window.postError) {
                        error.responseJSON.formulario = formulario;
                        postError(error.responseJSON);
                    } else {
                        mostrarToast('danger', 'Ocurri un error al procesar la solicitud.');
                    }
                }
            });
        } else if (camposHiddenVacios.length > 0) {
            if (window.cantidadesVacias) { cantidadesVacias(); }
        }
    });
}

/**
 * Limpiar el formulario.
 *
 * @returns {void}
 * @author Jos Manuel Jimnez
 */
function limpiarFormulario(formulario) {
    $(`#${formulario} input:not([name="_token"]):not([type="radio"])`).each(function () {
        $(this).val('');
    });
    $(`#${formulario} textarea:not(.chk-editor)`).val('').trigger('input');
    $(`#${formulario} .chk-editor`).val('');
    $(`#${formulario} select.select2-asincrono`).each(function () {
        limpiarOpcionesSelect2(this.id);
    });
    $(`#${formulario} select.select2:not(.select2-asincrono)`).val('').trigger('change').trigger('select2:select');
    $("#boton-guardar").text('Guardar');
    if (window.limpiarAdicional) { validoAdicional = limpiarAdicional(); }
    $('.previsualizar-imagen').remove();
    $(`#${formulario} .eliminar-archivo`).click();
}

