$(document).ready(function () {

    if ($('#formulario').length) {
        configurarFormulario('formulario');
    }

    let notificacionToast = localStorage.getItem('notificacionToast');
    if (notificacionToast != null) {
        notificacionToast = JSON.parse(localStorage.getItem('notificacionToast'));
        if (notificacionToast.hasOwnProperty('sucursal_creada') && notificacionToast.sucursal_creada) {
            if (window.abrirModalCrearAdministradorSucursal) {
                abrirModalCrearAdministradorSucursal(notificacionToast.id_sucursal, notificacionToast.nombre_comercial_sucursal);
            }
        } else {
            mostrarToast(notificacionToast.tipo_alerta, notificacionToast.texto_1);
        }
        localStorage.removeItem("notificacionToast");
    }
});

/**
 * Inicializar el campo de texto enriquecido.
 *
 * @type {ClassicEditor}
 * @returns {void}
 * @author Daniel Beltrn
 */
function inicializarEditorTexto(elemento_id) {
    ClassicEditor.create(document.querySelector(`#${elemento_id}`), {
        toolbar: {
            items: ['undo', 'redo', 'heading', 'bold', 'italic', 'link', 'imageUpload', 'blockQuote'],
            shouldNotGroupWhenFull: false
        }
    }).then(editor => {
        respuesta = editor;
        const barra_herramientas = editor.ui.view.toolbar.element;
        const editable = editor.ui.getEditableElement();
        const contenedor = editable.parentElement;

        // Mover la barra de herramientas al final del contenedor del editor
        contenedor.appendChild(barra_herramientas);

        // Opcional: Aadir una clase para ajustar el estilo si es necesario
        barra_herramientas.classList.add('toolbar-bottom');
    }).catch(error => {
        console.error(error);
    });
}

/**
 * Eliminar plan.
 *
 * @returns {void}
 * @author Sol Espitia
 */
function confirmarEliminar() {
    $.ajax({
        method: 'POST',
        url: url_eliminar + '/' + id_eliminar,
        data: {
            _method: 'DELETE',
            _token: $('meta[name="csrf-token"]').attr('content')
        }
    }).then(function (data) {
        mostrarToast('success', 'Plan eliminado correctamente');
        $('#modal-eliminar-general').modal('hide');

        id_eliminar = null;
        nombre_eliminar = null;
        url_eliminar = null;

        if (window.ejecutarDespuesEliminar) {
            ejecutarDespuesEliminar();
        }
    }).fail(function (error) {
        console.log(error);
    });
};

/**
 * abrir modal de Eliminar plan.
 * @param {number} id_eliminar_modal es el id del elemento a eliminar u objeto
 * @param {string} nombre_eliminar_modal es el nombre del elemento a eliminar u objeto
 * @param {string} url_eliminar_modal es la url para eliminar el elemento u objeto
 * @returns {void}
 * @author Sol Espitia
 */
function abrirModalEliminar(id_eliminar_modal, nombre_eliminar_modal, url_eliminar_modal) {
    $('#modal-eliminar-general').modal('show');
    id_eliminar = id_eliminar_modal;
    nombre_eliminar = nombre_eliminar_modal;
    url_eliminar = url_eliminar_modal;
    $('#nombre-eliminar').text(nombre_eliminar_modal);
}

