$(document).ready(function() {
    $('#modal-movimiento-8').on('shown.bs.modal', function() {
        mostrarConfiguracionesEtapaMovimientos();
    });

    $('#modal-movimiento-8').on('hidden.bs.modal', function() {
        $('#contenedor-archivos-preguntas-prefiltro').empty();
        $('#archivo_prueba').val('');
        archivos_preguntas = [];
    });

    // Validación de límite de caracteres en textarea de descripción
    $(document).on('input', '#movimiento_vacante_configuracion_id_20', function() {
        var longitud_actual = $(this).val().length;
        var limite_maximo = 1500;

        if (longitud_actual >= limite_maximo && !$(this).data('toast_mostrado')) {
            mostrarToast('warning', 'Has alcanzado el límite de 1500 caracteres', 'No puedes agregar más texto a la descripción.');
            $(this).data('toast_mostrado', true);
        }

        // Reiniciar el flag si el usuario borra texto
        if (longitud_actual < limite_maximo) {
            $(this).data('toast_mostrado', false);
        }
    });
});

