/**
 * Archivo para manejar la lógica del modal de solicitar documentos
 * @author José Manuel Jiménez
 * @author Andres Morales
 */

$(document).ready(function() {
    $('#modal-movimiento-8').on('shown.bs.modal', function() {
        mostrarConfiguracionesEtapaMovimientos();
    });

    $('#modal-movimiento-8').on('hidden.bs.modal', function() {
        $('#contenedor-archivos-preguntas-prefiltro').empty();
        $('#archivo_prueba').val('');
        archivos_preguntas = [];
    });
});

/**
 * Manejar el cambio del checkbox de adecuaciones medias en prueba técnica
 * Si se marca, automáticamente marca el de adecuaciones bajas
 *
 * @param {HTMLElement} checkbox Elemento checkbox
 * @returns {void}
 * @author Andres Morales
 */
function manejarCheckAdecuacionesMediasPruebaTecnica(checkbox) {
    var checkbox_bajas = $('#movimiento_vacante_configuracion_id_22');

    if ($(checkbox).prop('checked')) {
        checkbox_bajas.prop('checked', true);
    }
}

/**
 * Manejar el cambio del checkbox de adecuaciones bajas en prueba técnica
 * Si se desmarca, automáticamente desmarca el de adecuaciones medias
 *
 * @param {HTMLElement} checkbox Elemento checkbox
 * @returns {void}
 * @author Andres Morales
 */
function manejarCheckAdecuacionesBajasPruebaTecnica(checkbox) {
    var checkbox_medias = $('#movimiento_vacante_configuracion_id_23');

    if (!$(checkbox).prop('checked')) {
        checkbox_medias.prop('checked', false);
    }
}

