public function suplantarRolEmpresa(Request $request) {
    // Se valida si se está intentando suplantar un usuario con rol Superadmin, si es así se
    // restablece la sesión original.
    if ($this->validarSuplantacionUsuarioSuperadmin($request)) {
        $this->restablecerSesionOriginal();
        return;
    }

    $empresa_usuario = $this->empresa_usuario_repositorio->getUsuarioEmpresaRol($request);

    // Se valida si se está intentando suplantar a un usuario con rol Administrador General, si
    // es así y la empresa a la que pertenecería este usuario no tiene un usuario con dicho rol
    // se intentará suplantar entonces a un usuario con rol Propietario de la misma empresa.
    if (!isset($empresa_usuario) && $request->rol_id === $this->repositorio::ROL_ADMINISTRADOR_GENERAL_ID) {
        $request->merge(['rol_id' => $this->repositorio::ROL_PROPIETARIO_ID]);
        $empresa_usuario = $this->empresa_usuario_repositorio->getUsuarioEmpresaRol($request);
    }

    // Se valida si el usuario, el rol y la empresa que se están intentando suplantar existen en
    // la base datos y están relacionados entre sí.
    if (isset($empresa_usuario)) {
        $usuario_suplantado            = $this->existenDatosSuplantacionSesion();
        $empresa_usuario_id_suplantado = $empresa_usuario->usuario->empresa_usuario_id;
        $cambio_rol_empresa_original   = $usuario_suplantado ? $this->obtenerDeSesion('cambio_rol_empresa_original') : $this->obtenerDeSesion('cambio_rol_empresa');
        $empresa_original_id           = $usuario_suplantado ? $this->obtenerDeSesion('empresa_original_id') : $this->obtenerDeSesion('empresa_id');
        $rol_original_id               = $usuario_suplantado ? $this->obtenerDeSesion('rol_original_id') : $this->obtenerDeSesion('rol_id');
        $usuario_original_id           = $usuario_suplantado ? $this->obtenerDeSesion('usuario_original_id') : Auth::id();

        // Si ya existen datos de suplantación de usuario, es decir, ya se está suplantando la
        // sesión de otro usuario, entonces se eliminan los datos de está cuenta suplantada de
        // la sesión actual.
        if ($usuario_suplantado) {
            if ($this->existeEnSesion('empresa_usuario_id_suplantado')) {
                $this->usuario_repositorio->actualizar(Auth::id(), ['empresa_usuario_id' => $this->obtenerDeSesion('empresa_usuario_id_suplantado')]);
            }
            $this->removerVariosDeSesion(self::DATOS_SUPLANTACION_SESION);
        }

        // Se realiza la suplantación de sesión.
        Auth::logout();
        session()->invalidate();
        session()->regenerateToken();

        Auth::loginUsingId($empresa_usuario->usuario_id);
        session()->regenerate();

        $this->usuario_repositorio->actualizar($empresa_usuario->usuario_id, ['empresa_usuario_id' => $empresa_usuario->id]);

        // Se guardan los datos de la nueva sesión suplantada en la sesión actual.
        $this->guardarVariosEnSesion(compact(
            'empresa_usuario_id_suplantado',
            'cambio_rol_empresa_original',
            'empresa_original_id',
            'rol_original_id',
            'usuario_original_id'
        ));
    } else {
        $rol_suplantar     = $this->repositorio->getUno($request->rol_id);
        $empresa_suplantar = $this->empresa_repositorio->getUnoBasico($request->empresa_id);

        // Se lanza una excepción si no existe el rol o la empresa que se están intentando
        // suplantar.
        foreach (['rol_suplantar', 'empresa_suplantar'] as $variable) {
            if (!isset($$variable)) {
                throw new Exception("no existe {$variable}", 404);
            }
        }

        // Se lanza una excepción si el rol y la empresa a los que se están intentando suplantar
        // existen pero no están relacionados entre sí.
        throw new Exception('{"rol":"' . $rol_suplantar->name . '","empresa":"' . $empresa_suplantar->nombre_comercial . '"}', 404);
    }
}

/**
 * Validar si se está intentando realizar una suplantación de usuario Superadmin.
 *
 * @param Request $request Datos de la solicitud HTTP.
 * @return bool
 * @author Fahibram Cárcamo
 */
private function validarSuplantacionUsuarioSuperadmin(Request $request): bool {
    $usuario_suplantado  = $this->existenDatosSuplantacionSesion();
    $empresa_original_id = $usuario_suplantado ? $this->obtenerDeSesion('empresa_original_id') : $this->obtenerDeSesion('empresa_id');
    $rol_original_id     = $usuario_suplantado ? $this->obtenerDeSesion('rol_original_id') : $this->obtenerDeSesion('rol_id');

    return $request->rol_id == $this->repositorio::ROL_ADMINISTRADOR_GENERAL_ID &&
        $request->empresa_id == $this->empresa_repositorio::ID_EMPRESA_PSICOALIANZA &&
        $rol_original_id == $this->repositorio::ROL_SUPERADMIN_ID &&
        $empresa_original_id == $this->empresa_repositorio::ID_EMPRESA_PSICOALIANZA;
}

/**
 * Restablecer la sesión que había antes de la suplantación junto con todos sus datos.
 *
 * @return void
 * @author Fahibram Cárcamo
 */
public function restablecerSesionOriginal() {
    $empresa_usuario_id_suplantado = $this->obtenerDeSesion('empresa_usuario_id_suplantado');

